$().ready(function(){var a={classLaddaBtn:"ladda-button",urlResetVerify:"/account/reset"};$("#alertBox .close").click(function(){$(".alert").hide()});$("#btnResetVerify").click(function(){var e=Ladda.create($(this).get(0));e.start();var d=$("#hidToken").val();var c=$("#txtChallenge").val();var b=$("#txtPassword").val();$.ajax({url:a.urlResetVerify,type:"POST",dataType:"html",data:{token:d,challenge:c,password:b},success:function(g){var f=jQuery.parseJSON(g);console.log(f.success);if(f.success){ShowAlert(f.status,"Success")}else{ShowAlert(f.status,"Error")}e.stop()},error:function(f){ShowAlert(f.responseText,"Error");e.stop()}})})});function ShowAlert(b,a){if(a=="Success"){$("#alertBox").removeClass("alert-danger");$("#alertBox").addClass("alert-success")}else{if(a=="Error"){$("#alertBox").removeClass("alert-success");$("#alertBox").addClass("alert-danger")}}$("#alertType").text(a);$("#alertMessage").html(b);$("#alertBox").show()}function ResetNewPos(){var a=$("#selPosType");a.removeAttr("disabled");a.val("ALOHA");$("#txtWebServiceURL").val("");$("#txtConnectionString").val("");$("#div-webservice").show();$("#div-connectionstring").hide();$("#btnDeletePos").hide()};