$().ready(function(){var a={classLaddaBtn:"ladda-button",urlRemoveUser:"/restaurant/{id}/removeuser"};$("#dt-users").dataTable();$("#alertBox .close").click(function(){$(".alert").hide()});$(".removeUserBtn").click(function(){var b=Ladda.create($(this).get(0));b.start();var d=$("#spanSelectedRestaurantName").data("restaurant-id");var c=$(this).data("username");$.ajax({url:a.urlRemoveUser.replace("{id}",d),type:"PUT",dataType:"html",data:{username:c},success:function(f){var e=jQuery.parseJSON(f);b.stop();if(e.success){ShowAlert(e.status,"Success");window.location.reload()}else{ShowAlert(e.status,"Error")}},error:function(e){ShowAlert(e.responseText,"Error");b.stop()}})})});function ShowAlert(b,a){if(a=="Success"){$("#alertBox").removeClass("alert-danger");$("#alertBox").addClass("alert-success")}else{if(a=="Error"){$("#alertBox").removeClass("alert-success");$("#alertBox").addClass("alert-danger")}}$("#alertType").text(a);$("#alertMessage").text(b);$("#alertBox").show()};