$().ready(function(){var a={classLaddaBtn:"ladda-button",urlUserAdd:"/user/addexisting"};$("#alertBox .close").click(function(){$(".alert").hide()});$("#btnAddUser").click(function(){$("#frmAddUser").validate({rules:{txtUsername:"required"},submitHandler:function(e,d){d.preventDefault();var c=Ladda.create($("#btnAddUser").get(0));c.start();var g=$("#hidRestaurantId").val();var f=$("#txtUsername").val();var b=$("#selRestaurantRole option:selected").text();$.ajax({url:a.urlUserAdd,type:"POST",dataType:"html",data:{restaurantId:g,username:f,restaurantRole:b},success:function(i){var h=jQuery.parseJSON(i);console.log(h.success);if(h.success){ShowAlert(h.status,"Success")}else{ShowAlert(h.status,"Error")}c.stop()},error:function(h){ShowAlert(h.responseText,"Error");c.stop()}})}})})});function ShowAlert(b,a){if(a=="Success"){$("#alertBox").removeClass("alert-danger");$("#alertBox").addClass("alert-success")}else{if(a=="Error"){$("#alertBox").removeClass("alert-success");$("#alertBox").addClass("alert-danger")}}$("#alertType").text(a);$("#alertMessage").text(b);$("#alertBox").show()};